import { useState, useEffect } from 'react';
import axios from 'axios';
import PatientForm from './components/PatientForm';
import PatientDetails from './components/PatientDetails';
import AppointmentForm from './components/AppointmentForm';

const App = () => {
  const [isPatientModalOpen, setIsPatientModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [patients, setPatients] = useState([]); // This should hold the full list
  const API_BASE_URL = 'https://localhost:7126/api'; 

  useEffect(() => {
    axios.get(`${API_BASE_URL}/patients/all`).then(res => setPatients(res.data));
    try {
      axios.get(`${API_BASE_URL}/patients/all`).then(res => setPatients(res.data));
    } catch (error) {
      console.error("Error fetching patients", error);
    }
  }, []);
  const refreshPatientData = async (patientId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/patients/${patientId}`);
      setSelectedPatient(response.data);
    } catch (error) {
      console.error("Error fetching patient details", error);
    }
  };
  const handlePatientSubmit = async (patientData) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/patients`, patientData);
      setSelectedPatient(response.data);
      setView('details');
    } catch (error) {
      alert("Failed to create patient. Check backend validation.");
    }
  };

  const handleAppointmentSubmit = async (appointmentData) => {
    try {
      await axios.post(`${API_BASE_URL}/appointments`, appointmentData);
      // REFRESH: Fetch data again so the new appointment appears in the list 
      await refreshPatientData(appointmentData.patientId);
      setView('details');
    } catch (error) {
      alert("Error scheduling appointment.");
    }
  };

  const addAppointment = async (patient) => {
    try {
      setSelectedPatient(patient);
      setView('add-appointment');
    } catch (error) {
      alert("Error scheduling appointment.");
    }
  };

  return (
    <div className="container">
      <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px' }}>
        <h1>Clinic Management</h1>
        <button onClick={() => setIsPatientModalOpen(true)}>+ Create New Patient</button>
      </header>

      <table className="patient-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(p => (
            <tr key={p.id}>
              <td>{p.fullName}</td>
              <td>{p.address}</td>
              <td>
                <button onClick={() => { setSelectedPatient(p); setView('details'); }}>
                  View Profile
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modals */}
      {isPatientModalOpen && (
        <Modal onClose={() => setIsPatientModalOpen(false)}>
          <PatientForm 
            onSubmit={(data) => { handlePatientSubmit(data); setIsPatientModalOpen(false); }} 
          />
        </Modal>
      )}
    </div>
  );
};

export default App;
