import { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import PatientForm from './components/PatientForm';
import PatientDetails from './components/PatientDetails';
import AppointmentForm from './components/AppointmentForm';
import Modal from './components/Modal';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

const App = () => {
  const [view, setView] = useState('list'); 
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [isPatientModalOpen, setIsPatientModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [patients, setPatients] = useState([]); 

  const API_BASE_URL = 'https://localhost:7126/api'; 

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const res = await axios.get(`${API_BASE_URL}/patients/all`);
      setPatients(res.data);
    } catch (error) {
      toast.error(error);
    }
  };

  const refreshPatientData = async (patientId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/patients/${patientId}`);
      setSelectedPatient(response.data);
      fetchPatients();
    } catch (error) {
      toast.error(error);
    }
  };

  const deletePatientData = async (patientId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/patients/delete/${patientId}`);
      fetchPatients();
      toast.success("Patient deleted successfully!");
    } catch (error) {
      toast.error(error);
    }
  };

  const handlePatientSubmit = async (patientData) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/patients`, patientData);
      setSelectedPatient(response.data);
      setIsPatientModalOpen(false);
      setView('details');
      fetchPatients();
      toast.success("Patient created successfully!");
    } catch (error) {
      toast.error("Failed to create patient. Please check the required fields.");
    }
  };

  const handleAppointmentSubmit = async (appointmentData) => {
    try {
      await axios.post(`${API_BASE_URL}/appointments`, appointmentData);
      await refreshPatientData(appointmentData.patientId);
      setIsAppointmentModalOpen(false);
      toast.success("Appointment scheduled!");
    } catch (error) {
      toast.error("Could not schedule appointment. Slot might be unavailable.");
    }
  };

  return (
    <div className="container">
      {/* --- DASHBOARD VIEW --- */}
      {view === 'list' && (
        <>
          <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px' }}>
            <h1>Clinic Management</h1>
            <button onClick={() => setIsPatientModalOpen(true)}>+ Create New Patient</button>
          </header>

          <table className="patient-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Address</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {patients.map(p => (
                <tr key={p.id}>
                  <td>{p.fullName}</td>
                  <td>{p.address}</td>
                  <td>
                    <button onClick={() => { setSelectedPatient(p); setView('details'); }}>
                      View Profile
                    </button>
                    <button onClick={() => { DeletePatient(p);}}>
                      Delete Profile
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {view === 'details' && selectedPatient && (
        <>
          <button onClick={() => setView('list')}>‚Üê Back to List</button>
          <PatientDetails 
            patient={selectedPatient} 
            onAddAppointment={() => setIsAppointmentModalOpen(true)} 
          />
        </>
      )}

      {isPatientModalOpen && (
        <Modal title="New Patient" onClose={() => setIsPatientModalOpen(false)}>
          <PatientForm 
            onSubmit={handlePatientSubmit} 
            onCancel={() => setIsPatientModalOpen(false)} 
          />
        </Modal>
      )}

      {isAppointmentModalOpen && (
        <Modal title="Schedule Appointment" onClose={() => setIsAppointmentModalOpen(false)}>
          <AppointmentForm 
            patientId={selectedPatient.id}
            onSubmit={handleAppointmentSubmit}
            onCancel={() => setIsAppointmentModalOpen(false)}
          />
        </Modal>
      )}
      <ToastContainer position="bottom-right" autoClose={3000} />
    </div>
  );
};

export default App;
