import { useState, useEffect } from 'react';
import axios from 'axios';
import PatientForm from './components/PatientForm';
import PatientDetails from './components/PatientDetails';
import AppointmentForm from './components/AppointmentForm';

const App = () => {
  const [view, setView] = useState('list'); 
  const [patients, setPatients] = useState([]); 
  const [selectedPatient, setSelectedPatient] = useState(null);
  const API_BASE_URL = 'https://localhost:7126/api'; 

  useEffect(() => {
    axios.get(`${API_BASE_URL}/patients/all`).then(res => setPatients(res));
  }, []);
  const refreshPatientData = async (patientId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/patients/${patientId}`);
      setSelectedPatient(response.data);
    } catch (error) {
      console.error("Error fetching patient details", error);
    }
  };
  const handlePatientSubmit = async (patientData) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/patients`, patientData);
      setSelectedPatient(response.data);
      setView('details');
    } catch (error) {
      alert("Failed to create patient. Check backend validation.");
    }
  };

  const handleAppointmentSubmit = async (appointmentData) => {
    try {
      await axios.post(`${API_BASE_URL}/appointments`, appointmentData);
      // REFRESH: Fetch data again so the new appointment appears in the list 
      await refreshPatientData(appointmentData.patientId);
      setView('details');
    } catch (error) {
      alert("Error scheduling appointment.");
    }
  };

  return (
    <div className="App" style={{ padding: '20px' }}>
      {view === 'list' && (
        <div>
          <h1>Practice Management</h1>
          <button onClick={() => setView('create-patient')}>Create New Patient</button>
          {patients.map(p => <PatientDetails 
                                patient={p} 
                                onAddAppointment={() => setView('add-appointment')} 
        />)}
        </div>
      )}
      {view === 'create-patient' && (
        <PatientForm 
          onSubmit={handlePatientSubmit} 
          onCancel={() => setView('list')} 
        />
      )}
      {view === 'details' && (
        <PatientDetails 
          patient={selectedPatient} 
          onAddAppointment={() => setView('add-appointment')} 
        />
      )}
      {view === 'add-appointment' && (
        <AppointmentForm 
          patientId={selectedPatient.id}
          onSubmit={handleAppointmentSubmit}
          onCancel={() => setView('details')}
        />
      )}
    </div>
  );
};

export default App;
